import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [parent, setParent] = useState('');
  const [data, setData] = useState({
    path :  "",
    files: []
});

  useEffect(() => {
    fetch("http://localhost:1337/")
      .then(res => res.json())
      .then(
        (result) => {
            setParent('');
            setData(result);
        },
        (error) => {

        }
      )
    }, []);

    const clickLink = event => {
        event.preventDefault();
        fetch("http://localhost:1337/?path="+event.target.attributes.href.value)
            .then(res => res.json())
            .then(
              (result) => {
                    let linkArr = result.path.split('/');
                    linkArr.pop();
                    setParent(linkArr.join('/'));
                    setData(result);
              },
              (error) => {

              }
        );

    }

    const clickDownload = event => {
        event.preventDefault();
        fetch("http://localhost:1337/download?filePath?=" + event.target.attributes.href.value)
            .then(res => res.json())
            .then(
                (result) => {
                    let linkArr = result.path.split('/');
                    linkArr.pop();
                    setParent(linkArr.join('/'));
                    setData(result);
                },
                (error) => {

                }
            );
    }

  return (
    <div className="filesGrid">

      <div className="currentFolder">
            client: {data.path === '' ? '/' : data.path}
      </div>

      <ul className="filesList">
          {data.files.map(item =>{

              if (item.isDirectory) {
                return <li className="folder" key={item.name}>
                            <a href={data.path + '/' + item.name} onClick={clickLink}>
                            <span href={data.path + '/' + item.name}  className="material-icons">&#xe2c7;</span>
                              {item.name.toUpperCase()}
                            </a>
                        </li>
              }
              else {
                  return <li className="file"  key={item.name}>
                            <span className="material-icons">&#xe873;</span>
                            {item.name}
                            <a href={data.path + '/' + item.name} onClick={clickDownload}>
                                <span href={data.path + '/' + item.name} className="material-icons">&#xf090;</span>
                            </a>
                        </li>
              }

          })}
          </ul>

          <div>
              <a href={parent} onClick={clickLink}>
                  <span className="material-icons">&#xe5c4;</span>
                  Back
              </a>
          </div>

    </div>
  );
}

export default App;
