import React, { useEffect, useState } from 'react';
import './App.css';

function App() {

    const [data, setData] = useState({
        path: "",
        files: []
        });

    useEffect(() => {
        fetch("http://localhost:1337/")
            .then(res => res.json())
            .then(
                (result) => {
                    setData(result);
                },
                (error) => {

                }
            )
    }, []);

  return (
      <div className="file-manager">
          <ul className="folder-list">
              {data.files.map(item => {

                  if (item.dir) {
                      return <li>{item.name}</li>
                  } else {
                      return <li>{item.name}</li>
                  }

              }) }
          </ul>
      </div>
  );
}

export default App;
