import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [parent, setParent] = useState('');
  const [data, setData] = useState({
    path :  "",
    files: []
});

  useEffect(() => {
    fetch("http://localhost:1337/")
      .then(res => res.json())
      .then(
        (result) => {
            setParent('');
            setData(result);
        },
        (error) => {

        }
      )
}, []);

const clickHandler = event => {
    event.preventDefault();
    // console.log(event.target.attributes.href.value);
    fetch("http://localhost:1337/?path="+event.target.attributes.href.value)
        .then(res => res.json())
        .then(
          (result) => {
                let linkArr = result.path.split('/');
                console.log(linkArr);
                linkArr.pop();
                setParent(linkArr.join('/'));
                setData(result);
          },
          (error) => {

          }
        );
}

  return (
    <div className="file-manager">

      <div className="current-level">
            client: {data.path === '' ? '/' : data.path}
      </div>

      <ul className="folder-list">
          {data.files.map(item =>{

              if (item.dir) {
                return <li className="folder" key={item.name}>
                            <a href={data.path+'/'+item.name} onClick={clickHandler}>
                              <span className="material-icons">&#xe2c7;</span>
                              {item.name.toUpperCase()}
                            </a>
                        </li>
              }
              else {
                return <li key={item.name} className="file">
                          <span className="material-icons">&#xe873;</span>
                          {item.name}
                        </li>
              }

          })}
          </ul>

          <div>
              <a href={parent} onClick={clickHandler}>
                  <span className="material-icons">&#xe5c4;</span>
                  Back
              </a>
          </div>
    </div>
  );
}

export default App;
